import "../Styles/Hero.css"
import { useEffect, useState } from "react"

export default function Hero() {

    const [text1, setintroText1] = useState("")
    
    const introText1 = "<Welcome!>"

    useEffect(() => {
        const write1 = (text) => {
            let index = 0;
        
            function escreverCaractere() {
                if (index < text.length) {
                    newIntroText1 += text[index];
                    console.log(newIntroText1);
                    index++;
                    setTimeout(escreverCaractere, 200);  // Atraso de 200 milissegundos
                    return newIntroText1
                }
            }
        
            escreverCaractere();  // Inicia o processo chamando a função pela primeira vez
        }

        write1(introText1)
        
    },)

    

    return(

        <div className="hero">
            <div className="texts">
                {/*<img className = "profilepicture" src="/assets/guilhermeprofile.jpg" alt="foto do guilherme"></img>*/}
                <h1 className = "introtext1">{newIntroText1}</h1>
                <h1 className="introtext2">&#123;&#47;&#42; I'm Guilherme Taliberti...&#42;&#47;&#125;</h1>
                <h1 className="introtext3">&#47;&#47;And this is my Portifolio!</h1>
            </div>
        </div>
    )
}
