import "../Styles/Hero.css"
import React, { useState, useEffect } from 'react';

export default function Hero() {
    
    const introText1 = "<Welcome!>";
    const [newIntroText1, setNewIntroText1] = useState("");

    useEffect(() => {
        const write1 = (text) => {
            let index = 0;

            const intervalId = setInterval(() => {
                if (index < text.length) {
                    setNewIntroText1(prevText => prevText + text[index]);
                    index++;
                } else {
                    clearInterval(intervalId);
                }
            }, 200);

            return () => clearInterval(intervalId);
        };

        write1(introText1);
    }, []); 

    return(

        <div className="hero">
            <div className="texts">
                {/*<img className = "profilepicture" src="/assets/guilhermeprofile.jpg" alt="foto do guilherme"></img>*/}
                <h1 className = "introtext1">{newIntroText1}</h1>
                <h1 className="introtext2">&#123;&#47;&#42; I'm Guilherme Taliberti...&#42;&#47;&#125;</h1>
                <h1 className="introtext3">&#47;&#47;And this is my Portifolio!</h1>
            </div>
        </div>
    )
}
