import '../Styles/ProjectItem.css';
import { motion } from 'framer-motion';
import { useAnimatedVisibility } from '../Hooks/AnimationHooks';
// Criando uma função que irá receber o nome da tecnologia, a descrição, a imagem e o level
export default function ProjectItem({
  title,
  description,
  src,
  alt,
  tag,
  index,
}) {
  let tagColor;

  switch (tag) {
    case 'BackEnd':
      tagColor = 'red';
      break;
    case 'FrontEnd':
      tagColor = 'blue';
      break;
    case 'FullStack':
      tagColor = 'orange';
      break;
  }

  const tagstyle = {
    backgroundColor: tagColor,
  };

  // Determinar qual configuração de animação aplicar com base no índice
  const { ref, animateSettings } =
    index % 2 === 0
      ? useAnimatedVisibility(
          '-100px 0px',
          { hidden: { opacity: 0, x: -250 }, visible: { opacity: 1, x: 0 } },
          { duration: 1, delay: 0.5 },
        )
      : useAnimatedVisibility(
          '-100px 0px',
          { hidden: { opacity: 0, x: 250 }, visible: { opacity: 1, x: 0 } },
          { duration: 1, delay: 0.75 },
        );

  return (
    // Declaração da div do item. Os eventos onMouseEnter e onMouseLeave chamam as funções declaradas
    <motion.div {...animateSettings} className="projectitem" ref={ref}>
      <h1 className="projecttitle">{title}</h1>
      <div className="projectblock">
        <img className="projectimage" src={src} alt={alt}></img>
        <div className="projectcontent">
          <p className="projectdescription">{description}</p>
          <p className="projecttag" style={tagstyle}>
            {tag}
          </p>
        </div>
      </div>
    </motion.div>
  );
}
